  });

  return (
    <div style={{minHeight: '100vh', background: '#f9fafb'}}>
      <div style={{background: 'linear-gradient(90deg, #2563eb 0%, #4f46e5 100%)', color: 'white', padding: '1rem', boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'}}>
