import React from 'react';

const ConvocationsList = ({ 
  matchId, 
  joueurs, 
  convocations, 
  onConvoquer, 
  onDeconvoquer 
}) => {
  const isConvoque = (joueurId) => {
    return convocations.some(c => 
      c.id_match === matchId && 
      c.id_joueur === joueurId && 
      c.convoque === true
    );
  };

  const getStatut = (joueurId) => {
    const convoc = convocations.find(c => 
      c.id_match === matchId && 
      c.id_joueur === joueurId
    );
    return convoc?.statut || 'en_attente';
  };

  return (
    <div style={{ marginTop: '1.5rem' }}>
      <h3 style={{ fontSize: '1.125rem', fontWeight: '600', marginBottom: '1rem' }}>
        Gestion des convocations
      </h3>

      <div style={{ display: 'grid', gap: '0.5rem' }}>
        {joueurs.map(joueur => {
          const convoque = isConvoque(joueur.id);
          const statut = getStatut(joueur.id);

          return (
            <div 
              key={joueur.id}
              style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'space-between',
                padding: '1rem',
                background: '#f9fafb',
                borderRadius: '0.5rem'
              }}
            >
              <div>
                <p style={{ fontWeight: '600', color: '#1f2937', margin: 0 }}>
                  {joueur.nom}
                </p>
                <p style={{ fontSize: '0.75rem', color: '#6b7280', margin: 0 }}>
                  @{joueur.username}
                </p>
              </div>

              {convoque ? (
                <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                  <span style={{
                    padding: '0.5rem 1rem',
                    borderRadius: '9999px',
                    fontSize: '0.875rem',
                    fontWeight: '600',
                    background: statut === 'accepte' ? '#d1fae5' : statut === 'refuse' ? '#fee2e2' : '#fef3c7',
                    color: statut === 'accepte' ? '#065f46' : statut === 'refuse' ? '#991b1b' : '#92400e'
                  }}>
                    {statut === 'accepte' ? '✅ Acceptée' : statut === 'refuse' ? '❌ Refusée' : '⏳ En attente'}
                  </span>
                  <button
                    onClick={() => onDeconvoquer(matchId, joueur.id)}
                    style={{
                      padding: '0.5rem 1rem',
                      background: '#dc2626',
                      color: 'white',
                      border: 'none',
                      borderRadius: '0.5rem',
                      fontWeight: '600',
                      cursor: 'pointer',
                      fontSize: '0.875rem'
                    }}
                  >
                    Déconvoquer
                  </button>
                </div>
              ) : (
                <button
                  onClick={() => onConvoquer(matchId, joueur.id)}
                  style={{
                    padding: '0.5rem 1rem',
                    background: '#ff8800',
                    color: 'white',
                    border: 'none',
                    borderRadius: '0.5rem',
                    fontWeight: '600',
                    cursor: 'pointer',
                    fontSize: '0.875rem'
                  }}
                >
                  ✓ Convoquer
                </button>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default ConvocationsList;
