import { doc, setDoc, getDoc } from 'firebase/firestore';
import { db } from '../config/firebase';

const useNotesJoueurs = () => {
  const saveNote = async (joueurId, selectedMatch, noteInputs, setNoteInputs, notes, setNotes, setEditingNote, showNotification) => {
    const noteValue = noteInputs[joueurId];
    if (!noteValue || noteValue < 1 || noteValue > 9) {
      showNotification({ message: '❌ Note invalide : doit être entre 1 et 9', type: 'error' });
      return;
    }

    // Écrire dans Firestore
    const noteId = `${String(selectedMatch)}_${String(joueurId)}`;
    await setDoc(doc(db, 'notes', noteId), {
      id: noteId,
      matchId: String(selectedMatch),
      userId: String(joueurId),
      note: parseFloat(noteValue),
      createdAt: new Date()
    });

    // Mettre à jour local
    const newNotes = notes.filter(n => !(n.id_match === selectedMatch && n.id_joueur === joueurId));
    newNotes.push({ id_match: selectedMatch, id_joueur: joueurId, note: parseFloat(noteValue) });
    setNotes(newNotes);
    setEditingNote(null);
    setNoteInputs({ ...noteInputs, [joueurId]: '' });
    showNotification({ message: '✅ Note enregistrée', type: 'success' });
  };

  const saveButs = async (joueurId, selectedMatch, butInputs, setButInputs, buteurs, setButeurs, showNotification) => {
    const butsValue = parseInt(butInputs[joueurId]) || 0;

    // Récupérer stats existantes
