      id: statId,
      matchId: String(selectedMatch),
      userId: String(joueurId),
      buts: butsValue,
      tempsDeJeu: existingData.tempsDeJeu || 0,
      passesDecisives: existingData.passesDecisives || 0,
      createdAt: existingData.createdAt || new Date()
