    const newButeurs = buteurs.filter(b => !(b.id_match === selectedMatch && b.id_joueur === joueurId));
    if (butsValue > 0) {
      newButeurs.push({ id_match: selectedMatch, id_joueur: joueurId, buts: butsValue });
    }
    setButeurs(newButeurs);
    setButInputs({ ...butInputs, [joueurId]: '' });
    showNotification({ message: '✅ Buts enregistrés', type: 'success' });
  };

  const saveTemps = async (joueurId, selectedMatch, tempsInputs, setTempsInputs, tempsDeJeu, setTempsDeJeu, showNotification) => {
    const tempsValue = parseInt(tempsInputs[joueurId]) || 0;

    if (tempsValue < 0 || tempsValue > 120) {
      showNotification({ message: '❌ Temps de jeu invalide : 0-120 minutes', type: 'error' });
      return;
    }

    // Récupérer stats existantes
    const statId = `${String(selectedMatch)}_${String(joueurId)}`;
    const statDoc = doc(db, 'stats', statId);
    const existingStat = await getDoc(statDoc);

    const existingData = existingStat.exists() ? existingStat.data() : {};

    // Merge avec nouvelles données
    await setDoc(statDoc, {
      id: statId,
      matchId: String(selectedMatch),
      userId: String(joueurId),
      buts: existingData.buts || 0,
      tempsDeJeu: tempsValue,
      passesDecisives: existingData.passesDecisives || 0,
