    // Mettre à jour local
    const newTemps = tempsDeJeu.filter(t => !(t.id_match === selectedMatch && t.id_joueur === joueurId));
    if (tempsValue > 0) {
      newTemps.push({ id_match: selectedMatch, id_joueur: joueurId, temps: tempsValue });
    }
    setTempsDeJeu(newTemps);
    setTempsInputs({ ...tempsInputs, [joueurId]: '' });
    showNotification({ message: '✅ Temps de jeu enregistré', type: 'success' });
  };

  const savePasses = async (joueurId, selectedMatch, passesInputs, setPassesInputs, passesD, setPassesD, showNotification) => {
    const passesValue = parseInt(passesInputs[joueurId]) || 0;

    // Récupérer stats existantes
    const statId = `${String(selectedMatch)}_${String(joueurId)}`;
    const statDoc = doc(db, 'stats', statId);
    const existingStat = await getDoc(statDoc);

    const existingData = existingStat.exists() ? existingStat.data() : {};

    // Merge avec nouvelles données
    await setDoc(statDoc, {
      id: statId,
      matchId: String(selectedMatch),
      userId: String(joueurId),
      buts: existingData.buts || 0,
      tempsDeJeu: existingData.tempsDeJeu || 0,
