    if (passesValue > 0) {
      newPasses.push({ id_match: selectedMatch, id_joueur: joueurId, passes: passesValue });
    }
    setPassesD(newPasses);
    setPassesInputs({ ...passesInputs, [joueurId]: '' });
    showNotification({ message: '✅ Passes D enregistrées', type: 'success' });
  };

  return {
    saveNote,
    saveButs,
    saveTemps,
    savePasses
  };
};

export default useNotesJoueurs;
