import { collection, doc, setDoc, writeBatch, getDocs } from 'firebase/firestore';
import { db } from '../config/firebase';
import { lireGoogleSheets } from '../services/googleSheetsService';

export const migrateAllToFirestore = async () => {
  console.log('üöÄ D√âBUT MIGRATION');

  const results = {
    matches: { success: 0, errors: 0 },
    convocations: { success: 0, errors: 0 },
    notes: { success: 0, errors: 0 },
    stats: { success: 0, errors: 0 },
    evaluations: { success: 0, errors: 0 }
  };

  try {
    // 1. MATCHS
    console.log('üìÖ Matchs...');
    const matchs = await lireGoogleSheets('Matchs');
    const batch1 = writeBatch(db);

    for (const match of matchs) {
      const matchRef = doc(db, 'matches', String(match.id));
      batch1.set(matchRef, {
        id: String(match.id),
        date: new Date(match.date),
        adversaire: match.adversaire,
        domicile: match.domicile === 'true',
        statut: match.statut,
        scoreEquipe: parseInt(match.scoreEquipe) || 0,
        scoreAdversaire: parseInt(match.scoreAdversaire) || 0,
        createdAt: new Date()
      });
      results.matches.success++;
    }
    await batch1.commit();
    console.log(`‚úÖ ${results.matches.success} matchs`);

    // 2. CONVOCATIONS
    console.log('üë• Convocations...');
    const convocations = await lireGoogleSheets('Convocations');
    const batch2 = writeBatch(db);

    for (const convoc of convocations) {
      const convocId = `${String(convoc.id_match)}_${String(convoc.id_joueur)}`;
      const convocRef = doc(db, 'convocations', convocId);
      batch2.set(convocRef, {
        id: convocId,
        matchId: String(convoc.id_match),
        userId: String(convoc.id_joueur),
        convoque: convoc.convoque === 'true',
        statut: convoc.statut || 'en_attente',
        createdAt: new Date()
      });
      results.convocations.success++;
    }
    await batch2.commit();
    console.log(`‚úÖ ${results.convocations.success} convocations`);

    // 3. NOTES
    console.log('‚≠ê Notes...');
    const notes = await lireGoogleSheets('Notes');
    const batch3 = writeBatch(db);

    for (const note of notes) {
      const noteId = `${String(note.id_match)}_${String(note.id_joueur)}`;
      const noteRef = doc(db, 'notes', noteId);
      batch3.set(noteRef, {
        id: noteId,
        matchId: String(note.id_match),
        userId: String(note.id_joueur),
        note: parseFloat(note.note) || 0,
        createdAt: new Date()
      });
      results.notes.success++;
    }
    await batch3.commit();
    console.log(`‚úÖ ${results.notes.success} notes`);

    // 4. STATS (buts + temps + passes)
    console.log('üìä Stats...');
    const buteurs = await lireGoogleSheets('Buteurs');
    const tempsDeJeu = await lireGoogleSheets('TempsDeJeu');
    const passesD = await lireGoogleSheets('PassesD');

    const statsMap = new Map();

    buteurs.forEach(but => {
      const key = `${String(but.id_match)}_${String(but.id_joueur)}`;
      if (!statsMap.has(key)) {
        statsMap.set(key, { matchId: String(but.id_match), userId: String(but.id_joueur), buts: 0, tempsDeJeu: 0, passesDecisives: 0 });
      }
      statsMap.get(key).buts = parseInt(but.buts) || 0;
    });

    tempsDeJeu.forEach(temps => {
      const key = `${String(temps.id_match)}_${String(temps.id_joueur)}`;
      if (!statsMap.has(key)) {
        statsMap.set(key, { matchId: String(temps.id_match), userId: String(temps.id_joueur), buts: 0, tempsDeJeu: 0, passesDecisives: 0 });
      }
      statsMap.get(key).tempsDeJeu = parseInt(temps.temps) || 0;
    });

    passesD.forEach(passe => {
      const key = `${String(passe.id_match)}_${String(passe.id_joueur)}`;
      if (!statsMap.has(key)) {
        statsMap.set(key, { matchId: String(passe.id_match), userId: String(passe.id_joueur), buts: 0, tempsDeJeu: 0, passesDecisives: 0 });
      }
      statsMap.get(key).passesDecisives = parseInt(passe.passes) || 0;
    });

    const batch4 = writeBatch(db);
    for (const [key, stat] of statsMap.entries()) {
      const statRef = doc(db, 'stats', key);
      batch4.set(statRef, {
        id: key,
        matchId: stat.matchId,
        userId: stat.userId,
        buts: stat.buts,
        tempsDeJeu: stat.tempsDeJeu,
        passesDecisives: stat.passesDecisives,
        createdAt: new Date()
      });
      results.stats.success++;
    }
    await batch4.commit();
    console.log(`‚úÖ ${results.stats.success} stats`);

    // 5. √âVALUATIONS
    console.log('üí¨ √âvaluations...');
    const evaluations = await lireGoogleSheets('EvaluationsCoach');
    const batch5 = writeBatch(db);

    for (const evaluation of evaluations) {
      const evalId = `${String(evaluation.id_joueur)}_${String(evaluation.id_coach)}`;
      const evalRef = doc(db, 'evaluations', evalId);
      batch5.set(evalRef, {
        id: evalId,
        evaluateurId: String(evaluation.id_joueur),
        coachId: String(evaluation.id_coach),
        entrainements: parseFloat(evaluation.entrainements) || 0,
        coaching: parseFloat(evaluation.coaching) || 0,
        relation: parseFloat(evaluation.relation) || 0,
        noteGlobale: parseFloat(evaluation.note_globale) || 0,
        updatedAt: new Date(evaluation.derniere_modification) || new Date(),
        createdAt: new Date()
      });
      results.evaluations.success++;
    }
    await batch5.commit();
    console.log(`‚úÖ ${results.evaluations.success} √©valuations`);

    console.log('\n‚úÖ MIGRATION TERMIN√âE !');
    console.log('R√©sultats:', results);
    return results;

  } catch (error) {
    console.error('‚ùå ERREUR:', error);
    throw error;
  }
};
