import React, { useEffect } from 'react';

// Hooks personnalisés
import { useFirebaseAuth } from './hooks/useFirebaseAuth';
import useNotification from './hooks/useNotification';
import useGoogleSheets from './hooks/useGoogleSheets';
import useMatchManagement from './hooks/useMatchManagement';
import useEvaluations from './hooks/useEvaluations';
import usePlayerStats from './hooks/usePlayerStats';
import useConvocations from './hooks/useConvocations';
import useNotesJoueurs from './hooks/useNotesJoueurs';

// Composants partagés
import NotificationToast from './components/Shared/NotificationToast';
import LoadingScreen from './components/Shared/LoadingScreen';
import ScoreModal from './components/Shared/ScoreModal';
import ConfirmModal from './components/Shared/ConfirmModal';

// Composants Auth
import LoginForm from './components/Auth/LoginForm';

// Composants Coach
import CoachView from './components/Coach/CoachView';

// Composants Joueur
import JoueurView from './components/Joueur/JoueurView';

function App() {
  // Auth
  const { user, loading: authLoading, handleLogin: performLogin, handleLogout } = useFirebaseAuth();

  // States pour le formulaire de login
  const [loginUsername, setLoginUsername] = React.useState('');
  const [loginPassword, setLoginPassword] = React.useState('');

  // Notifications
  const { notification, showNotification } = useNotification();

  // Google Sheets
  const {
    joueurs,
    matchs,
    setMatchs,
    convocations,
    setConvocations,
    notes,
    setNotes,
    buteurs,
    setButeurs,
    tempsDeJeu,
    setTempsDeJeu,
    passesD,
    setPassesD,
    evaluationsCoach,
    setEvaluationsCoach,
    loading,
    chargerDonnees
  } = useGoogleSheets(user);

  // Match Management
  const {
    selectedMatch,
    setSelectedMatch,
    showScoreModal,
    setShowScoreModal,
    matchToPlay,
    scoreEquipeInput,
    setScoreEquipeInput,
    scoreAdversaireInput,
    setScoreAdversaireInput,
    showConfirmModal,
    marquerCommeJoue,
    confirmerScoreEtJouer,
    demanderRepasserEnAttente,
    confirmerRepasserEnAttente,
    annulerRepasserEnAttente
  } = useMatchManagement();

  // Evaluations
  const {
    evalIsma,
    setEvalIsma,
    evalAdam,
    setEvalAdam,
    loadEvaluations,
    saveEvaluationIsma,
    saveEvaluationAdam
  } = useEvaluations();

  // Player Stats
  const {
    noteInputs,
    setNoteInputs,
    butInputs,
    setButInputs,
    tempsInputs,
    setTempsInputs,
    passesInputs,
    setPassesInputs,
    editingNote,
    setEditingNote,
    handleNoteChange,
    handleButsChange,
    handleTempsChange,
    handlePassesChange
  } = usePlayerStats();

  // Convocations
  const {
    convoquerJoueur,
    deconvoquerJoueur,
    accepterConvocation,
    refuserConvocation
  } = useConvocations();

  // Notes Joueurs
  const {
    saveAll
  } = useNotesJoueurs();

  // Charger les données au démarrage
  useEffect(() => {
    chargerDonnees();
  }, []);

  // Charger les évaluations du joueur
  useEffect(() => {
    loadEvaluations(user, evaluationsCoach);
  }, [user, evaluationsCoach]);

  // Gestion du login
  const handleLogin = async () => {
    const result = await performLogin(loginUsername, loginPassword);
    if (result.success) {
      // Connexion OK
    } else {
      alert('Identifiants incorrects');
    }
  };

  // Gestion logout avec reset
  const logout = () => {
    handleLogout();
    setSelectedMatch(null);
  };

  // Loading screen
  if (loading || authLoading) {
    return (
      <ClaudeProvider>
        <div className="app">
          {notification && <NotificationToast message={notification.message} type={notification.type} onClose={() => showNotification(null)} />}
          <LoadingScreen />
        </div>
      </ClaudeProvider>
    );
  }

  // Login screen
  if (!user) {
    return (
      <ClaudeProvider>
        <div className="app">
          {notification && <NotificationToast message={notification.message} type={notification.type} onClose={() => showNotification(null)} />}
          <LoginForm
            loginUsername={loginUsername}
            setLoginUsername={setLoginUsername}
            loginPassword={loginPassword}
            setLoginPassword={setLoginPassword}
            onLogin={handleLogin}
          />
        </div>
      </ClaudeProvider>
    );
  }

  // Coach/Coach Adjoint View
  if (user.role === 'coach' || user.role === 'coach_adjoint') {
    return (
      <ClaudeProvider>
        <div className="app">
          {notification && <NotificationToast message={notification.message} type={notification.type} onClose={() => showNotification(null)} />}
          <CoachView
            user={user}
            joueurs={joueurs}
            matchs={matchs}
            setMatchs={setMatchs}
            convocations={convocations}
            setConvocations={setConvocations}
            notes={notes}
            setNotes={setNotes}
            buteurs={buteurs}
            setButeurs={setButeurs}
            tempsDeJeu={tempsDeJeu}
            setTempsDeJeu={setTempsDeJeu}
            passesD={passesD}
            setPassesD={setPassesD}
            evaluationsCoach={evaluationsCoach}
            selectedMatch={selectedMatch}
            setSelectedMatch={setSelectedMatch}
            noteInputs={noteInputs}
            setNoteInputs={setNoteInputs}
            butInputs={butInputs}
            setButInputs={setButInputs}
            tempsInputs={tempsInputs}
            setTempsInputs={setTempsInputs}
            passesInputs={passesInputs}
            setPassesInputs={setPassesInputs}
            editingNote={editingNote}
            setEditingNote={setEditingNote}
            handleNoteChange={handleNoteChange}
            handleButsChange={handleButsChange}
            handleTempsChange={handleTempsChange}
            handlePassesChange={handlePassesChange}
            convoquerJoueur={(matchId, joueurId) => convoquerJoueur(matchId, joueurId, convocations, setConvocations)}
            deconvoquerJoueur={(matchId, joueurId) => deconvoquerJoueur(matchId, joueurId, convocations, setConvocations)}
            marquerCommeJoue={(matchId) => marquerCommeJoue(matchId, matchs)}
            repasserEnAttente={(matchId) => demanderRepasserEnAttente(matchId)}
            saveAll={(joueurId) => saveAll(joueurId, selectedMatch, noteInputs, setNoteInputs, butInputs, setButInputs, tempsInputs, setTempsInputs, passesInputs, setPassesInputs, notes, setNotes, buteurs, setButeurs, tempsDeJeu, setTempsDeJeu, passesD, setPassesD, setEditingNote, showNotification)}
            onLogout={logout}
          />
          <ScoreModal
            show={showScoreModal}
            match={matchToPlay}
            scoreEquipe={scoreEquipeInput}
            scoreAdversaire={scoreAdversaireInput}
            onScoreEquipeChange={setScoreEquipeInput}
            onScoreAdversaireChange={setScoreAdversaireInput}
            onConfirm={() => confirmerScoreEtJouer(matchs, setMatchs, showNotification)}
            onCancel={() => setShowScoreModal(false)}
          />
          <ConfirmModal
            show={showConfirmModal}
            title="⚠️ Repasser en attente"
            message="Êtes-vous sûr ? Toutes les notes et statistiques de ce match seront supprimées."
            onConfirm={() => confirmerRepasserEnAttente(matchs, setMatchs, notes, setNotes, setButeurs, setTempsDeJeu, setPassesD)}
            onCancel={annulerRepasserEnAttente}
          />
        </div>
      </ClaudeProvider>
    );
  }

  // Joueur View
  return (
    <ClaudeProvider>
      <div className="app">
        {notification && <NotificationToast message={notification.message} type={notification.type} onClose={() => showNotification(null)} />}
        <JoueurView
          user={user}
          matchs={matchs}
          convocations={convocations}
          setConvocations={setConvocations}
          notes={notes}
          buteurs={buteurs}
          tempsDeJeu={tempsDeJeu}
          passesD={passesD}
          evaluationsCoach={evaluationsCoach}
          setEvaluationsCoach={setEvaluationsCoach}
          evalIsma={evalIsma}
          setEvalIsma={setEvalIsma}
          evalAdam={evalAdam}
          setEvalAdam={setEvalAdam}
          accepterConvocation={(matchId, joueurId) => accepterConvocation(matchId, joueurId, user, convocations, setConvocations)}
          refuserConvocation={(matchId, joueurId) => refuserConvocation(matchId, joueurId, user, convocations, setConvocations)}
          saveEvaluationIsma={() => saveEvaluationIsma(user, evaluationsCoach, setEvaluationsCoach, showNotification)}
          saveEvaluationAdam={() => saveEvaluationAdam(user, evaluationsCoach, setEvaluationsCoach, showNotification)}
          onLogout={logout}
        />
      </div>
    </ClaudeProvider>
  );
}

export default App;
