const useNotesJoueurs = () => {
  const saveAll = (joueurId, selectedMatch, noteInputs, setNoteInputs, butInputs, setButInputs, tempsInputs, setTempsInputs, passesInputs, setPassesInputs, notes, setNotes, buteurs, setButeurs, tempsDeJeu, setTempsDeJeu, passesD, setPassesD, setEditingNote, showNotification) => {
    const noteValue = noteInputs[joueurId];
    const butsValue = butInputs[joueurId];
    const tempsValue = tempsInputs[joueurId];
    const passesValue = passesInputs[joueurId];

    // Validations
    if (noteValue && (noteValue < 1 || noteValue > 9)) {
      showNotification('❌ Note invalide : doit être entre 1 et 9', 'error');
      return;
    }

    if (butsValue && (butsValue < 0 || butsValue > 15)) {
      showNotification('❌ Buts invalides : maximum 15 buts', 'error');
      return;
    }

    if (tempsValue && (tempsValue < 0 || tempsValue > 120)) {
      showNotification('❌ Temps invalide : maximum 120 minutes', 'error');
      return;
    }

    if (passesValue && (passesValue < 0 || passesValue > 15)) {
      showNotification('❌ Passes D invalides : maximum 15 passes', 'error');
      return;
    }

    // Sauvegarder note
    if (noteValue) {
      const newNotes = notes.filter(n => !(n.id_match === selectedMatch && n.id_joueur === joueurId));
      newNotes.push({ id_match: selectedMatch, id_joueur: joueurId, note: parseFloat(noteValue) });
      setNotes(newNotes);
    }

    // Sauvegarder buts
    const newButeurs = buteurs.filter(b => !(b.id_match === selectedMatch && b.id_joueur === joueurId));
    if (butsValue && butsValue > 0) {
      newButeurs.push({ id_match: selectedMatch, id_joueur: joueurId, buts: parseInt(butsValue) });
    }
    setButeurs(newButeurs);

    // Sauvegarder temps
    const newTemps = tempsDeJeu.filter(t => !(t.id_match === selectedMatch && t.id_joueur === joueurId));
    if (tempsValue && tempsValue > 0) {
      newTemps.push({ id_match: selectedMatch, id_joueur: joueurId, temps: parseInt(tempsValue) });
    }
    setTempsDeJeu(newTemps);

    // Sauvegarder passes
    const newPasses = passesD.filter(p => !(p.id_match === selectedMatch && p.id_joueur === joueurId));
    if (passesValue && passesValue > 0) {
      newPasses.push({ id_match: selectedMatch, id_joueur: joueurId, passes: parseInt(passesValue) });
    }
    setPassesD(newPasses);

    // Reset
    setEditingNote(null);
    setNoteInputs({ ...noteInputs, [joueurId]: '' });
    setButInputs({ ...butInputs, [joueurId]: '' });
    setTempsInputs({ ...tempsInputs, [joueurId]: '' });
    setPassesInputs({ ...passesInputs, [joueurId]: '' });

    showNotification('✅ Statistiques enregistrées', 'success');
  };

  return {
    saveAll
  };
};

export default useNotesJoueurs;
