import { useState } from 'react';

const useMatchManagement = () => {
  const [selectedMatch, setSelectedMatch] = useState(null);
  const [showScoreModal, setShowScoreModal] = useState(false);
  const [matchToPlay, setMatchToPlay] = useState(null);
  const [scoreEquipeInput, setScoreEquipeInput] = useState('');
  const [scoreAdversaireInput, setScoreAdversaireInput] = useState('');
  const [showConfirmModal, setShowConfirmModal] = useState(false);
  const [matchToReset, setMatchToReset] = useState(null);

  const marquerCommeJoue = (matchId, matchs) => {
    const match = matchs.find(m => m.id === matchId);
    if (!match) return;

    setMatchToPlay(match);
    setShowScoreModal(true);
  };

  const confirmerScoreEtJouer = (matchs, setMatchs, showNotification) => {
    if (!matchToPlay) return;

    const scoreEquipe = parseInt(scoreEquipeInput) || 0;
    const scoreAdversaire = parseInt(scoreAdversaireInput) || 0;

    const updatedMatchs = matchs.map(m =>
      m.id === matchToPlay.id
        ? { ...m, statut: 'joue', score_equipe: scoreEquipe, score_adversaire: scoreAdversaire }
        : m
    );
    setMatchs(updatedMatchs);

    showNotification('✅ Match marqué comme joué', 'success');
    setShowScoreModal(false);
    setMatchToPlay(null);
    setScoreEquipeInput('');
    setScoreAdversaireInput('');
  };

  const demanderRepasserEnAttente = (matchId) => {
    setMatchToReset(matchId);
    setShowConfirmModal(true);
  };

  const confirmerRepasserEnAttente = (matchs, setMatchs, notes, setNotes, setButeurs, setTempsDeJeu, setPassesD) => {
    if (!matchToReset) return;

    // Supprimer notes et stats
    setNotes(notes.filter(n => n.id_match !== matchToReset));
    setButeurs(prev => prev.filter(b => b.id_match !== matchToReset));
    setTempsDeJeu(prev => prev.filter(t => t.id_match !== matchToReset));
    setPassesD(prev => prev.filter(p => p.id_match !== matchToReset));

    const updatedMatchs = matchs.map(m =>
      m.id === matchToReset
        ? { ...m, statut: 'avenir', score_equipe: null, score_adversaire: null }
        : m
    );
    setMatchs(updatedMatchs);

    setShowConfirmModal(false);
    setMatchToReset(null);
  };

  const annulerRepasserEnAttente = () => {
    setShowConfirmModal(false);
    setMatchToReset(null);
  };

  return {
    selectedMatch,
    setSelectedMatch,
    showScoreModal,
    setShowScoreModal,
    matchToPlay,
    scoreEquipeInput,
    setScoreEquipeInput,
    scoreAdversaireInput,
    setScoreAdversaireInput,
    showConfirmModal,
    marquerCommeJoue,
    confirmerScoreEtJouer,
    demanderRepasserEnAttente,
    confirmerRepasserEnAttente,
    annulerRepasserEnAttente
  };
};

export default useMatchManagement;
