import { useState, useEffect, useRef } from 'react';
import {
  collection,
  getDocs,
  doc,
  writeBatch
} from 'firebase/firestore';
import { db } from '../config/firebase';

export const useFirestore = (user) => {
  const [joueurs, setJoueurs] = useState([]);
  const [matchs, setMatchs] = useState([]);
  const [convocations, setConvocations] = useState([]);
  const [notes, setNotes] = useState([]);
  const [buteurs, setButeurs] = useState([]);
  const [tempsDeJeu, setTempsDeJeu] = useState([]);
  const [passesD, setPassesD] = useState([]);
  const [evaluationsCoach, setEvaluationsCoach] = useState([]);
  const [loading, setLoading] = useState(true);

  const hasInitialized = useRef(false);

  // ============= READ FUNCTIONS =============

  const loadJoueurs = async () => {
    try {
      const usersSnap = await getDocs(collection(db, 'users'));
      const joueursData = usersSnap.docs.map(d => ({
        id: d.data().id || d.id,
        nom: d.data().nom,
        username: d.data().username,
        role: d.data().role
      }));
      setJoueurs(joueursData);
      return joueursData;
    } catch (error) {
      console.error('❌ Erreur chargement joueurs:', error);
      return [];
    }
  };

  const loadMatchs = async () => {
    try {
      const matchsSnap = await getDocs(collection(db, 'matchs'));
      const matchsData = matchsSnap.docs.map(d => {
        const data = d.data();
        return {
          id: data.id || d.id,
          date: data.date || '',
          adversaire: data.adversaire || '',
          domicile: data.domicile || 'false',
          statut: data.statut || 'avenir',
          scoreEquipe: data.scoreEquipe || null,
          scoreAdversaire: data.scoreAdversaire || null
        };
      });
      matchsData.sort((a, b) => {
        const dateA = a.date.split('/').reverse().join('-');
        const dateB = b.date.split('/').reverse().join('-');
        return new Date(dateA) - new Date(dateB);
      });
      setMatchs(matchsData);
      return matchsData;
    } catch (error) {
      console.error('❌ Erreur chargement matchs:', error);
      return [];
    }
  };

  const loadConvocations = async () => {
    try {
      const convocsSnap = await getDocs(collection(db, 'convocations'));
      const convocsData = convocsSnap.docs.map(d => ({
        id_match: d.data().id_match,
        id_joueur: d.data().id_joueur,
        convoque: d.data().convoque,
        statut: d.data().statut
      }));
      setConvocations(convocsData);
      return convocsData;
    } catch (error) {
      console.error('❌ Erreur chargement convocations:', error);
      return [];
    }
  };

  const loadNotes = async () => {
    try {
      const notesSnap = await getDocs(collection(db, 'notes'));
      const notesData = notesSnap.docs.map(d => {
        const noteStr = String(d.data().note || '0').replace(',', '.'); // Remplacer virgule par point
        return {
          id_match: d.data().id_match,
          id_joueur: d.data().id_joueur,
          note: parseFloat(noteStr) || 0
        };
      });
      setNotes(notesData);
      return notesData;
    } catch (error) {
      console.error('❌ Erreur chargement notes:', error);
      return [];
    }
  };

  const loadStats = async () => {
    try {
      const statsSnap = await getDocs(collection(db, 'stats'));
      const buteursArr = [];
      const tempsArr = [];
      const passesArr = [];

      statsSnap.docs.forEach(d => {
        const data = d.data();
        const matchId = data.id_match;
        const userId = data.id_joueur;

        // Convertir en nombres
        const buts = parseInt(data.buts) || 0;
        const temps = parseInt(data.temps) || 0;
        const passes = parseInt(data.passes) || 0;

        if (buts > 0) {
          buteursArr.push({ id_match: matchId, id_joueur: userId, buts });
        }
        if (temps > 0) {
          tempsArr.push({ id_match: matchId, id_joueur: userId, temps });
        }
        if (passes > 0) {
          passesArr.push({ id_match: matchId, id_joueur: userId, passes });
        }
      });

      setButeurs(buteursArr);
      setTempsDeJeu(tempsArr);
      setPassesD(passesArr);

      return { buteursArr, tempsArr, passesArr };
    } catch (error) {
      console.error('❌ Erreur chargement stats:', error);
      return { buteursArr: [], tempsArr: [], passesArr: [] };
    }
  };

  const loadEvaluations = async () => {
    try {
      const evalsSnap = await getDocs(collection(db, 'evaluations'));
      const evalsData = evalsSnap.docs.map(d => {
        const data = d.data();
        return {
          id_joueur: data.id_joueur,
          id_coach: data.id_coach,
          entrainements: parseFloat(String(data.entrainements || '0').replace(',', '.')) || 0,
          coaching: parseFloat(String(data.coaching || '0').replace(',', '.')) || 0,
          relation: parseFloat(String(data.relation || '0').replace(',', '.')) || 0,
          note_globale: parseFloat(String(data.note_globale || '0').replace(',', '.')) || 0
        };
      });
      setEvaluationsCoach(evalsData);
      return evalsData;
    } catch (error) {
      console.error('❌ Erreur chargement évaluations:', error);
      return [];
    }
  };

  // ============= WRITE FUNCTIONS =============

  const saveMatchs = async (matchsData) => {
    if (!user || user.role !== 'coach') return;

    try {
      const batch = writeBatch(db);

      for (const match of matchsData) {
        const matchRef = doc(db, 'matchs', match.id.toString());
        batch.set(matchRef, {
          id: match.id,
          date: match.date,
          adversaire: match.adversaire,
          domicile: match.domicile,
          statut: match.statut,
          scoreEquipe: match.scoreEquipe || null,
          scoreAdversaire: match.scoreAdversaire || null,
          updatedAt: new Date().toISOString()
        });
      }

      await batch.commit();
    } catch (error) {
      console.error('❌ Erreur sauvegarde matchs:', error);
      throw error;
    }
  };

  const saveConvocations = async (convocsData) => {
    if (!user || user.role !== 'coach') return;
    if (!convocsData || convocsData.length === 0) return; // Skip si vide

    try {
      // Filtrer les convocations valides (pas de undefined)
      const validConvocs = convocsData.filter(c =>
        c.id_match !== undefined &&
        c.id_joueur !== undefined
      );

      if (validConvocs.length === 0) return; // Skip si aucune valide

      // Supprimer toutes les convocations existantes
      const existingSnap = await getDocs(collection(db, 'convocations'));
      const batch1 = writeBatch(db);
      existingSnap.docs.forEach(d => {
        batch1.delete(d.ref);
      });
      await batch1.commit();

      // Créer les nouvelles convocations
      const batch2 = writeBatch(db);
      validConvocs.forEach((convoc) => {
        const convocRef = doc(db, 'convocations', `${convoc.id_match}_${convoc.id_joueur}`);
        batch2.set(convocRef, {
          id_match: convoc.id_match,
          id_joueur: convoc.id_joueur,
          convoque: convoc.convoque,
          statut: convoc.statut,
          updatedAt: new Date().toISOString()
        });
      });

      await batch2.commit();
    } catch (error) {
      console.error('❌ Erreur sauvegarde convocations:', error);
      throw error;
    }
  };

  const saveNotes = async (notesData) => {
    if (!user || user.role !== 'coach') return;
    if (!notesData || notesData.length === 0) return; // Skip si vide

    try {
      // Filtrer les notes valides (pas de undefined)
      const validNotes = notesData.filter(n =>
        n.id_match !== undefined &&
        n.id_joueur !== undefined
      );

      if (validNotes.length === 0) return; // Skip si aucune valide

      // Supprimer les notes existantes
      const existingSnap = await getDocs(collection(db, 'notes'));
      const batch1 = writeBatch(db);
      existingSnap.docs.forEach(d => {
        batch1.delete(d.ref);
      });
      await batch1.commit();

      // Créer les nouvelles notes
      const batch2 = writeBatch(db);
      validNotes.forEach(note => {
        const noteRef = doc(db, 'notes', `${note.id_match}_${note.id_joueur}`);
        batch2.set(noteRef, {
          id_match: note.id_match,
          id_joueur: note.id_joueur,
          note: note.note,
          updatedAt: new Date().toISOString()
        });
      });

      await batch2.commit();
    } catch (error) {
      console.error('❌ Erreur sauvegarde notes:', error);
      throw error;
    }
  };

  const saveStats = async (buteursData, tempsData, passesData) => {
    if (!user || user.role !== 'coach') return;

    // Skip si tous les tableaux sont vides
    if ((!buteursData || buteursData.length === 0) &&
        (!tempsData || tempsData.length === 0) &&
        (!passesData || passesData.length === 0)) {
      return;
    }

    try {
      // Supprimer les stats existantes
      const existingSnap = await getDocs(collection(db, 'stats'));
      const batch1 = writeBatch(db);
      existingSnap.docs.forEach(d => {
        batch1.delete(d.ref);
      });
      await batch1.commit();

      // Créer un map pour regrouper les stats par match + joueur
      const statsMap = new Map();

      // Filtrer et ajouter les buteurs valides
      (buteursData || []).filter(b => b.id_match !== undefined && b.id_joueur !== undefined).forEach(b => {
        const key = `${b.id_match}_${b.id_joueur}`;
        if (!statsMap.has(key)) {
          statsMap.set(key, { id_match: b.id_match, id_joueur: b.id_joueur, buts: 0, temps: 0, passes: 0 });
        }
        statsMap.get(key).buts = b.buts;
      });

      // Filtrer et ajouter les temps de jeu valides
      (tempsData || []).filter(t => t.id_match !== undefined && t.id_joueur !== undefined).forEach(t => {
        const key = `${t.id_match}_${t.id_joueur}`;
        if (!statsMap.has(key)) {
          statsMap.set(key, { id_match: t.id_match, id_joueur: t.id_joueur, buts: 0, temps: 0, passes: 0 });
        }
        statsMap.get(key).temps = t.temps;
      });

      // Filtrer et ajouter les passes valides
      (passesData || []).filter(p => p.id_match !== undefined && p.id_joueur !== undefined).forEach(p => {
        const key = `${p.id_match}_${p.id_joueur}`;
        if (!statsMap.has(key)) {
          statsMap.set(key, { id_match: p.id_match, id_joueur: p.id_joueur, buts: 0, temps: 0, passes: 0 });
        }
        statsMap.get(key).passes = p.passes;
      });

      // Skip si aucune stat valide
      if (statsMap.size === 0) return;

      // Sauvegarder toutes les stats
      const batch2 = writeBatch(db);
      statsMap.forEach((stat, key) => {
        const statRef = doc(db, 'stats', key);
        batch2.set(statRef, {
          id_match: stat.id_match,
          id_joueur: stat.id_joueur,
          buts: stat.buts,
          temps: stat.temps,
          passes: stat.passes,
          updatedAt: new Date().toISOString()
        });
      });

      await batch2.commit();
    } catch (error) {
      console.error('❌ Erreur sauvegarde stats:', error);
      throw error;
    }
  };

  const saveEvaluations = async (evalsData) => {
    try {
      // Supprimer les évaluations existantes
      const existingSnap = await getDocs(collection(db, 'evaluations'));
      const batch1 = writeBatch(db);
      existingSnap.docs.forEach(d => {
        batch1.delete(d.ref);
      });
      await batch1.commit();

      // Créer les nouvelles évaluations
      const batch2 = writeBatch(db);
      evalsData.forEach(evaluation => {
        const evalRef = doc(db, 'evaluations', `${evaluation.id_joueur}_${evaluation.id_coach}`);
        batch2.set(evalRef, {
          id_joueur: evaluation.id_joueur,
          id_coach: evaluation.id_coach,
          entrainements: evaluation.entrainements || 0,
          coaching: evaluation.coaching || 0,
          relation: evaluation.relation || 0,
          note_globale: evaluation.note_globale || 0,
          updatedAt: new Date().toISOString()
        });
      });

      await batch2.commit();
    } catch (error) {
      console.error('❌ Erreur sauvegarde évaluations:', error);
      throw error;
    }
  };

  // ============= INITIAL LOAD =============

  useEffect(() => {
    if (!user || hasInitialized.current) return;

    const loadData = async () => {
      try {
        setLoading(true);
        await Promise.all([
          loadJoueurs(),
          loadMatchs(),
          loadConvocations(),
          loadNotes(),
          loadStats(),
          loadEvaluations()
        ]);
        hasInitialized.current = true;
        setLoading(false);
      } catch (error) {
        console.error('❌ Erreur chargement Firestore:', error);
        setLoading(false);
      }
    };

    loadData();
  }, [user]);

  // ============= AUTO-SAVE EFFECTS =============

  // Auto-save matchs (coach only)
  useEffect(() => {
    if (!hasInitialized.current || !user || user.role !== 'coach' || matchs.length === 0) return;
    saveMatchs(matchs).catch(err => console.error('Auto-save matchs failed:', err));
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [matchs, user]);

  // Auto-save convocations (coach only)
  useEffect(() => {
    if (!hasInitialized.current || !user || user.role !== 'coach' || convocations.length === 0) return;
    saveConvocations(convocations).catch(err => console.error('Auto-save convocations failed:', err));
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [convocations, user]);

  // Auto-save notes (coach only)
  useEffect(() => {
    if (!hasInitialized.current || !user || user.role !== 'coach' || notes.length === 0) return;
    saveNotes(notes).catch(err => console.error('Auto-save notes failed:', err));
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [notes, user]);

  // Auto-save stats (coach only)
  useEffect(() => {
    if (!hasInitialized.current || !user || user.role !== 'coach') return;
    if (buteurs.length === 0 && tempsDeJeu.length === 0 && passesD.length === 0) return;
    saveStats(buteurs, tempsDeJeu, passesD).catch(err => console.error('Auto-save stats failed:', err));
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [buteurs, tempsDeJeu, passesD, user]);

  // Auto-save evaluations (tous les joueurs)
  useEffect(() => {
    if (!hasInitialized.current || !user || evaluationsCoach.length === 0) return;
    saveEvaluations(evaluationsCoach).catch(err => console.error('Auto-save evaluations failed:', err));
  }, [evaluationsCoach, user]);

  return {
    joueurs,
    matchs,
    setMatchs,
    convocations,
    setConvocations,
    notes,
    setNotes,
    buteurs,
    setButeurs,
    tempsDeJeu,
    setTempsDeJeu,
    passesD,
    setPassesD,
    evaluationsCoach,
    setEvaluationsCoach,
    loading,
    loadConvocations,
    loadMatchs,
    loadNotes,
    loadStats,
    loadEvaluations
  };
};
