import { doc, setDoc, deleteDoc, updateDoc } from 'firebase/firestore';
import { db } from '../config/firebase';

const useConvocations = (loadConvocations) => {
  const convoquerJoueur = async (matchId, joueurId) => {
    const convocId = `${String(matchId)}_${String(joueurId)}`;
    await setDoc(doc(db, 'convocations', convocId), {
      id: convocId,
      matchId: String(matchId),
      userId: String(joueurId),
      convoque: true,
      statut: 'en_attente',
      createdAt: new Date()
    });
    await loadConvocations();
  };

  const deconvoquerJoueur = async (matchId, joueurId) => {
    const convocId = `${String(matchId)}_${String(joueurId)}`;
    await deleteDoc(doc(db, 'convocations', convocId));
    await loadConvocations();
  };

  const accepterConvocation = async (matchId, joueurId) => {
    const convocId = `${String(matchId)}_${String(joueurId)}`;
    await updateDoc(doc(db, 'convocations', convocId), { statut: 'accepte' });
    await loadConvocations();
  };

  const refuserConvocation = async (matchId, joueurId) => {
    const convocId = `${String(matchId)}_${String(joueurId)}`;
    await updateDoc(doc(db, 'convocations', convocId), { statut: 'refuse' });
    await loadConvocations();
  };

  return { convoquerJoueur, deconvoquerJoueur, accepterConvocation, refuserConvocation };
};

export default useConvocations;
