/**
 * Système de classement des joueurs basé sur une formule pondérée
 *
 * Formule: Score = (Buts × 0.4) + (Passes D × 0.25) + (Note moyenne × 0.25) + (Temps de jeu/90 × 0.1)
 *
 * Pondérations:
 * - Buts: 40%
 * - Passes décisives: 25%
 * - Note moyenne: 25%
 * - Temps de jeu: 10% (normalisé sur 90 minutes)
 */

import { getMoyenneJoueur } from './playerActions';
import { getTotalButs, getTotalTemps, getTotalPasses } from './statsCalculator';

/**
 * Calcule le score global d'un joueur selon la formule pondérée
 * @param {Object} joueur - Le joueur
 * @param {Array} notes - Toutes les notes
 * @param {Array} buteurs - Tous les buteurs
 * @param {Array} tempsDeJeu - Tous les temps de jeu
 * @param {Array} passesD - Toutes les passes décisives
 * @returns {number} Score global du joueur
 */
export const calculatePlayerScore = (joueur, notes, buteurs, tempsDeJeu, passesD) => {
  // 1. Récupérer les stats du joueur
  const totalButs = getTotalButs(buteurs, joueur.id);
  const totalPasses = getTotalPasses(passesD, joueur.id);
  const totalTemps = getTotalTemps(tempsDeJeu, joueur.id);
  const moyenne = getMoyenneJoueur(notes, joueur.id);

  // 2. Gérer le cas où le joueur n'a pas de note
  const noteNormalisee = moyenne === '-' ? 0 : parseFloat(moyenne);

  // 3. Normaliser le temps de jeu (temps total / 90 minutes)
  // Un joueur qui a joué 90 minutes aura un score de 1 pour cette composante
  const tempsNormalise = totalTemps / 90;

  // 4. FORMULE SPÉCIALE POUR LES GARDIENS
  // Les gardiens n'ont pas de buts ni de passes décisives
  // Formule gardien: Score = (Note moyenne × 50%) + (Temps de jeu/90 × 50%)
  if (joueur.role === 'gardien') {
    const score = noteNormalisee * 0.5 +
    // Note moyenne: 50%
    tempsNormalise * 0.5 // Temps de jeu: 50%
    ;
    return score;
  }

  // 5. FORMULE STANDARD POUR LES JOUEURS DE CHAMP
  // Score = (Buts × 40%) + (Passes D × 25%) + (Note moyenne × 25%) + (Temps/90 × 10%)
  const score = totalButs * 0.4 +
  // Buts: 40%
  totalPasses * 0.25 +
  // Passes D: 25%
  noteNormalisee * 0.25 +
  // Note moyenne: 25%
  tempsNormalise * 0.1 // Temps de jeu: 10%
  ;
  return score;
};

/**
 * Trie les joueurs par score décroissant (meilleur en premier)
 * @param {Array} joueurs - Liste des joueurs
 * @param {Array} notes - Toutes les notes
 * @param {Array} buteurs - Tous les buteurs
 * @param {Array} tempsDeJeu - Tous les temps de jeu
 * @param {Array} passesD - Toutes les passes décisives
 * @returns {Array} Joueurs triés avec leur score
 */
export const rankPlayers = (joueurs, notes, buteurs, tempsDeJeu, passesD) => {
  return [...joueurs].map(joueur => ({
    ...joueur,
    score: calculatePlayerScore(joueur, notes, buteurs, tempsDeJeu, passesD)
  })).sort((a, b) => b.score - a.score); // Tri décroissant
};

