import React from 'react';
import { rankPlayers } from '../../utils/playerRanking';
import { getMoyenneJoueur } from '../../utils/playerActions';
import { getTotalButs, getTotalTemps, getTotalPasses } from '../../utils/statsCalculator';

const PodiumTop3 = ({ joueurs, notes, buteurs, tempsDeJeu, passesD }) => {
  // Obtenir le classement complet
  const joueursClasses = rankPlayers(joueurs, notes, buteurs, tempsDeJeu, passesD);

  // Prendre les 3 premiers
  const top3 = joueursClasses.slice(0, 3);

  if (top3.length === 0) {
    return null;
  }

  // Fonction pour obtenir les m√©dailles/couleurs
  const getPodiumStyle = (position) => {
    const styles = {
      0: { // 1er - Or
        bg: 'linear-gradient(135deg, #fbbf24 0%, #f59e0b 100%)',
        borderColor: '#f59e0b',
        icon: 'ü•á',
        height: '180px',
        order: 2 // Centre
      },
      1: { // 2√®me - Argent
        bg: 'linear-gradient(135deg, #e5e7eb 0%, #9ca3af 100%)',
        borderColor: '#9ca3af',
        icon: 'ü•à',
        height: '150px',
        order: 1 // Gauche
      },
      2: { // 3√®me - Bronze
        bg: 'linear-gradient(135deg, #f97316 0%, #ea580c 100%)',
        borderColor: '#ea580c',
        icon: 'ü•â',
        height: '120px',
        order: 3 // Droite
      }
    };
    return styles[position] || styles[2];
  };

  return (
    <div style={{background: 'white', padding: '1.5rem', borderRadius: '0.75rem', boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)', marginBottom: '1.5rem'}}>
      <h2 style={{fontSize: '1.25rem', fontWeight: 'bold', marginBottom: '1.5rem', color: '#1f2937', textAlign: 'center'}}>
        üèÜ Podium des meilleurs joueurs
      </h2>

      <div style={{display: 'flex', justifyContent: 'center', alignItems: 'flex-end', gap: '1rem', marginBottom: '1.5rem', flexWrap: 'wrap'}}>
        {top3.map((joueur, index) => {
          const style = getPodiumStyle(index);
          const moyenne = getMoyenneJoueur(notes, joueur.id);
          const totalButs = getTotalButs(buteurs, joueur.id);
          const totalTemps = getTotalTemps(tempsDeJeu, joueur.id);
          const totalPasses = getTotalPasses(passesD, joueur.id);

          return (
            <div
              key={joueur.id}
              style={{
                order: style.order,
                flex: '1 1 150px',
                maxWidth: '200px',
                minWidth: '150px'
              }}
            >
              {/* M√©daille */}
              <div style={{textAlign: 'center', fontSize: '3rem', marginBottom: '0.5rem'}}>
                {style.icon}
              </div>

              {/* Carte joueur */}
              <div style={{
                background: style.bg,
                borderRadius: '0.75rem',
                padding: '1rem',
                textAlign: 'center',
                border: `3px solid ${style.borderColor}`,
                boxShadow: '0 4px 15px rgba(0, 0, 0, 0.15)',
                height: style.height,
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'space-between'
              }}>
                {/* Nom */}
                <div>
                  <div style={{fontSize: '1.125rem', fontWeight: 'bold', color: 'white', marginBottom: '0.25rem'}}>
                    {joueur.nom}
                  </div>
                  <div style={{fontSize: '0.75rem', color: 'white', opacity: 0.9}}>
                    {joueur.role === 'gardien' ? 'Gardien' : 'Joueur'}
                  </div>
                </div>

                {/* Score */}
                <div style={{margin: '0.5rem 0'}}>
                  <div style={{fontSize: '2rem', fontWeight: 'bold', color: 'white'}}>
                    {joueur.score.toFixed(2)}
                  </div>
                  <div style={{fontSize: '0.75rem', color: 'white', opacity: 0.8}}>
                    points
                  </div>
                </div>

                {/* Stats rapides */}
                <div style={{display: 'flex', gap: '0.5rem', justifyContent: 'center', flexWrap: 'wrap'}}>
                  {moyenne !== '-' && (
                    <span style={{fontSize: '0.75rem', background: 'rgba(255,255,255,0.2)', color: 'white', padding: '0.25rem 0.5rem', borderRadius: '9999px'}}>
                      {moyenne}/10
                    </span>
                  )}
                  {joueur.role !== 'gardien' && totalButs > 0 && (
                    <span style={{fontSize: '0.75rem', background: 'rgba(255,255,255,0.2)', color: 'white', padding: '0.25rem 0.5rem', borderRadius: '9999px'}}>
                      {totalButs}‚öΩ
                    </span>
                  )}
                </div>
              </div>

              {/* Position */}
              <div style={{
                textAlign: 'center',
                marginTop: '0.5rem',
                fontSize: '0.875rem',
                fontWeight: '600',
                color: '#6b7280'
              }}>
                {index === 0 ? '1er' : index === 1 ? '2√®me' : '3√®me'}
              </div>
            </div>
          );
        })}
      </div>

      {/* L√©gende explicative */}
      <div style={{background: '#f9fafb', padding: '1rem', borderRadius: '0.5rem', fontSize: '0.875rem', color: '#6b7280', textAlign: 'center'}}>
        Classement bas√© sur: Buts (40%), Passes D (25%), Note moyenne (25%), Temps de jeu (10%)
        {top3.some(j => j.role === 'gardien') && (
          <div style={{marginTop: '0.25rem', fontSize: '0.75rem'}}>
            * Gardiens: Note moyenne (50%), Temps de jeu (50%)
          </div>
        )}
      </div>
    </div>
  );
};

export default PodiumTop3;
