import { useState } from 'react';

const useEvaluations = () => {
  const [evalIsma, setEvalIsma] = useState({
    technicite: '',
    vision: '',
    collectif: '',
    mental: ''
  });

  const [evalAdam, setEvalAdam] = useState({
    technicite: '',
    vision: '',
    collectif: '',
    mental: ''
  });

  const loadEvaluations = (user, evaluationsCoach) => {
    if (!user || user.role !== 'joueur') return;

    const evalIsmaData = evaluationsCoach.find(e => e.id_joueur === user.id && e.evaluateur === 'isma');
    const evalAdamData = evaluationsCoach.find(e => e.id_joueur === user.id && e.evaluateur === 'adam');

    if (evalIsmaData) {
      setEvalIsma({
        technicite: evalIsmaData.technicite || '',
        vision: evalIsmaData.vision || '',
        collectif: evalIsmaData.collectif || '',
        mental: evalIsmaData.mental || ''
      });
    }

    if (evalAdamData) {
      setEvalAdam({
        technicite: evalAdamData.technicite || '',
        vision: evalAdamData.vision || '',
        collectif: evalAdamData.collectif || '',
        mental: evalAdamData.mental || ''
      });
    }
  };

  const saveEvaluationIsma = (user, evaluationsCoach, setEvaluationsCoach, showNotification) => {
    if (!user) return;

    const newEvals = evaluationsCoach.filter(e => !(e.id_joueur === user.id && e.evaluateur === 'isma'));
    newEvals.push({
      id_joueur: user.id,
      evaluateur: 'isma',
      technicite: parseFloat(evalIsma.technicite) || 0,
      vision: parseFloat(evalIsma.vision) || 0,
      collectif: parseFloat(evalIsma.collectif) || 0,
      mental: parseFloat(evalIsma.mental) || 0
    });
    setEvaluationsCoach(newEvals);
    showNotification('✅ Évaluation Ismaël enregistrée', 'success');
  };

  const saveEvaluationAdam = (user, evaluationsCoach, setEvaluationsCoach, showNotification) => {
    if (!user) return;

    const newEvals = evaluationsCoach.filter(e => !(e.id_joueur === user.id && e.evaluateur === 'adam'));
    newEvals.push({
      id_joueur: user.id,
      evaluateur: 'adam',
      technicite: parseFloat(evalAdam.technicite) || 0,
      vision: parseFloat(evalAdam.vision) || 0,
      collectif: parseFloat(evalAdam.collectif) || 0,
      mental: parseFloat(evalAdam.mental) || 0
    });
    setEvaluationsCoach(newEvals);
    showNotification('✅ Évaluation Adam enregistrée', 'success');
  };

  return {
    evalIsma,
    setEvalIsma,
    evalAdam,
    setEvalAdam,
    loadEvaluations,
    saveEvaluationIsma,
    saveEvaluationAdam
  };
};

export default useEvaluations;
