import { useState, useEffect } from 'react';
import { collection, query, getDocs, doc, setDoc, updateDoc, deleteDoc } from 'firebase/firestore';
import { db } from '../config/firebase';

export const useFirestore = (user) => {
  const [joueurs, setJoueurs] = useState([]);
  const [matchs, setMatchs] = useState([]);
  const [convocations, setConvocations] = useState([]);
  const [notes, setNotes] = useState([]);
  const [stats, setStats] = useState([]);
  const [evaluations, setEvaluations] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user) return;

    const loadData = async () => {
      try {
        // Charger users
        const usersSnap = await getDocs(collection(db, 'users'));
        setJoueurs(usersSnap.docs.map(d => ({ ...d.data(), id: d.data().id || d.id })));

// Charger matches
const matchesSnap = await getDocs(collection(db, 'matches'));
const matchesData = matchesSnap.docs.map(d => ({
  id: d.data().id,
  date: d.data().date.toDate().toISOString().split('T')[0],
  adversaire: d.data().adversaire,
  domicile: d.data().domicile ? 'true' : 'false',
  statut: d.data().statut,
  scoreEquipe: d.data().scoreEquipe || 0,
  scoreAdversaire: d.data().scoreAdversaire || 0
}));
matchesData.sort((a, b) => new Date(a.date) - new Date(b.date));
setMatchs(matchesData);

        // Charger convocations
        const convocsSnap = await getDocs(collection(db, 'convocations'));
        setConvocations(convocsSnap.docs.map(d => ({
          id_match: d.data().matchId,
          id_joueur: d.data().userId,
          convoque: d.data().convoque,
          statut: d.data().statut
        })));

        // Charger notes
        const notesSnap = await getDocs(collection(db, 'notes'));
        setNotes(notesSnap.docs.map(d => ({
          id_match: d.data().matchId,
          id_joueur: d.data().userId,
          note: d.data().note
        })));

        // Charger stats et séparer en 3 arrays
        const statsSnap = await getDocs(collection(db, 'stats'));
        const buteursArr = [];
        const tempsArr = [];
        const passesArr = [];

        statsSnap.docs.forEach(d => {
          const data = d.data();
          if (data.buts > 0) {
            buteursArr.push({ id_match: data.matchId, id_joueur: data.userId, buts: data.buts });
          }
          if (data.tempsDeJeu > 0) {
            tempsArr.push({ id_match: data.matchId, id_joueur: data.userId, temps: data.tempsDeJeu });
          }
          if (data.passesDecisives > 0) {
            passesArr.push({ id_match: data.matchId, id_joueur: data.userId, passes: data.passesDecisives });
          }
        });

        setStats({ buteurs: buteursArr, tempsDeJeu: tempsArr, passesD: passesArr });

        // Charger évaluations
        const evalsSnap = await getDocs(collection(db, 'evaluations'));
        setEvaluations(evalsSnap.docs.map(d => ({
          id_joueur: d.data().evaluateurId,
          id_coach: d.data().coachId,
          entrainements: d.data().entrainements,
          coaching: d.data().coaching,
          relation: d.data().relation,
          note_globale: d.data().noteGlobale,
          derniere_modification: d.data().updatedAt?.toDate()
        })));

        setLoading(false);
      } catch (error) {
        console.error('Erreur chargement:', error);
        setLoading(false);
      }
    };

    loadData();
  }, [user]);
// Fonctions CRUD
const updateConvocation = async (matchId, userId, updates) => {
  const convocId = `${matchId}_${userId}`;
  await updateDoc(doc(db, 'convocations', convocId), updates);
};

const updateNote = async (matchId, userId, note) => {
  const noteId = `${matchId}_${userId}`;
  await setDoc(doc(db, 'notes', noteId), {
    id: noteId,
    matchId: String(matchId),
    userId: String(userId),
    note: parseFloat(note),
    createdAt: new Date()
  });
};

// Rechargement convocation après acceptation ou refus
const reloadConvocations = async () => {
  const convocsSnap = await getDocs(collection(db, 'convocations'));
  setConvocations(convocsSnap.docs.map(d => ({
    id_match: d.data().matchId,
    id_joueur: d.data().userId,
    convoque: d.data().convoque,
    statut: d.data().statut
  })));
};

const updateStats = async (matchId, userId, stats) => {
  const statId = `${matchId}_${userId}`;
  await setDoc(doc(db, 'stats', statId), {
    id: statId,
    matchId: String(matchId),
    userId: String(userId),
    buts: stats.buts || 0,
    tempsDeJeu: stats.temps || 0,
    passesDecisives: stats.passes || 0,
    createdAt: new Date()
  });
};
  return {
    joueurs,
    matchs,
    convocations,
    notes,
    buteurs: stats.buteurs || [],
    tempsDeJeu: stats.tempsDeJeu || [],
    passesD: stats.passesD || [],
    evaluationsCoach: evaluations,
    loading,
    updateConvocation,
updateNote,
updateStats,
reloadConvocations,
    setNotes,
    setConvocations,
    // TODO: fonctions CRUD
  };
};
