import { useState } from 'react';

const useEvaluations = () => {
  const [evalIsma, setEvalIsma] = useState({
    entrainements: '',
    coaching: '',
    relation: ''
  });

  const [evalAdam, setEvalAdam] = useState({
    entrainements: '',
    coaching: '',
    relation: ''
  });

  const loadEvaluations = (user, evaluationsCoach) => {
    if (!user || user.role !== 'joueur') return;

    const evalIsmaData = evaluationsCoach.find(e => e.id_joueur === user.id && e.id_coach === '1');
    const evalAdamData = evaluationsCoach.find(e => e.id_joueur === user.id && e.id_coach === '27'); // Adam a l'ID 27

    if (evalIsmaData) {
      setEvalIsma({
        entrainements: evalIsmaData.entrainements || '',
        coaching: evalIsmaData.coaching || '',
        relation: evalIsmaData.relation || ''
      });
    }

    if (evalAdamData) {
      setEvalAdam({
        entrainements: evalAdamData.entrainements || '',
        coaching: evalAdamData.coaching || '',
        relation: evalAdamData.relation || ''
      });
    }
  };

  const saveEvaluationIsma = (user, evaluationsCoach, setEvaluationsCoach, showNotification) => {
    if (!user) return;

    const newEvals = evaluationsCoach.filter(e => !(e.id_joueur === user.id && e.id_coach === '1'));

    const entrainements = parseFloat(evalIsma.entrainements) || 0;
    const coaching = parseFloat(evalIsma.coaching) || 0;
    const relation = parseFloat(evalIsma.relation) || 0;
    const note_globale = ((entrainements + coaching + relation) / 3).toFixed(2);

    newEvals.push({
      id_joueur: user.id,
      id_coach: '1',
      entrainements,
      coaching,
      relation,
      note_globale: parseFloat(note_globale)
    });
    setEvaluationsCoach(newEvals);
    showNotification('✅ Évaluation Ismaël enregistrée', 'success');
  };

  const saveEvaluationAdam = (user, evaluationsCoach, setEvaluationsCoach, showNotification) => {
    if (!user) return;

    const newEvals = evaluationsCoach.filter(e => !(e.id_joueur === user.id && e.id_coach === '27')); // Adam a l'ID 27

    const entrainements = parseFloat(evalAdam.entrainements) || 0;
    const coaching = parseFloat(evalAdam.coaching) || 0;
    const relation = parseFloat(evalAdam.relation) || 0;
    const note_globale = ((entrainements + coaching + relation) / 3).toFixed(2);

    newEvals.push({
      id_joueur: user.id,
      id_coach: '27', // Adam a l'ID 27
      entrainements,
      coaching,
      relation,
      note_globale: parseFloat(note_globale)
    });
    setEvaluationsCoach(newEvals);
    showNotification('✅ Évaluation Adam enregistrée', 'success');
  };

  return {
    evalIsma,
    setEvalIsma,
    evalAdam,
    setEvalAdam,
    loadEvaluations,
    saveEvaluationIsma,
    saveEvaluationAdam
  };
};

export default useEvaluations;
