import { useState, useEffect } from 'react';
import { 
  signInWithEmailAndPassword, 
  createUserWithEmailAndPassword,
  signOut as firebaseSignOut,
  onAuthStateChanged 
} from 'firebase/auth';
import { doc, getDoc, setDoc } from 'firebase/firestore';
import { auth, db } from '../config/firebase';
import { lireGoogleSheets } from '../services/googleSheetsService';

export const useFirebaseAuth = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Ã‰couter les changements d'auth
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (firebaseUser) => {
      if (firebaseUser) {
        // RÃ©cupÃ©rer les donnÃ©es utilisateur depuis Firestore
        const userDoc = await getDoc(doc(db, 'users', firebaseUser.uid));
        if (userDoc.exists()) {
          setUser({ uid: firebaseUser.uid, ...userDoc.data() });
        }
      } else {
        setUser(null);
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  const handleLogin = async (username, password) => {
    try {
      console.log('ðŸ” Tentative login pour:', username);

      // Charger les utilisateurs depuis Google Sheets
      const joueurs = await lireGoogleSheets('Joueurs');
      const joueur = joueurs.find(j => 
        j.username.toLowerCase() === username.toLowerCase() && 
        j.password === password
      );

      // VÃ©rifier les identifiants dans Google Sheets
      if (!joueur) {
        console.log('âŒ Identifiants incorrects dans Google Sheets');
        return { success: false, error: 'Identifiants incorrects' };
      }

      console.log('âœ… Utilisateur trouvÃ© dans Google Sheets:', joueur.nom);

      // CrÃ©er email Firebase
      const email = `${username.toLowerCase()}@esccappelle.com`;

      try {
        // Essayer de se connecter Ã  Firebase
        console.log('ðŸ”¥ Connexion Firebase...');
        const userCredential = await signInWithEmailAndPassword(auth, email, password);
        console.log('âœ… ConnectÃ© Ã  Firebase:', userCredential.user.uid);

        // VÃ©rifier si le document Firestore existe
        const userDoc = await getDoc(doc(db, 'users', userCredential.user.uid));
        if (!userDoc.exists()) {
          console.log('ðŸ“ Document Firestore manquant, crÃ©ation...');
          await setDoc(doc(db, 'users', userCredential.user.uid), {
            id: joueur.id,
            nom: joueur.nom,
            username: joueur.username,
            role: joueur.role,
            email: email,
            createdAt: new Date()
          });
          console.log('âœ… Document Firestore crÃ©Ã©');
        }

        return { success: true };

      } catch (loginError) {
        // Si l'utilisateur n'existe pas dans Firebase, le crÃ©er
        if (loginError.code === 'auth/user-not-found' || loginError.code === 'auth/invalid-credential') {
          console.log('ðŸ†• CrÃ©ation compte Firebase...');
          const userCredential = await createUserWithEmailAndPassword(auth, email, password);
          console.log('âœ… Compte Firebase crÃ©Ã©:', userCredential.user.uid);

          // CrÃ©er le document Firestore
          await setDoc(doc(db, 'users', userCredential.user.uid), {
            id: joueur.id,
            nom: joueur.nom,
            username: joueur.username,
            role: joueur.role,
            email: email,
            createdAt: new Date()
          });
          console.log('âœ… Document Firestore crÃ©Ã©');

          return { success: true };
        }
        throw loginError;
      }
    } catch (error) {
      console.error('âŒ Erreur login:', error);
      return { success: false, error: error.message };
    }
  };

  const handleLogout = async () => {
    try {
      await firebaseSignOut(auth);
      setUser(null);
    } catch (error) {
      console.error('Erreur logout:', error);
    }
  };

  return {
    user,
    loading,
    handleLogin,
    handleLogout
  };
};
