import { useState, useEffect } from 'react';
import {
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  signOut as firebaseSignOut,
  onAuthStateChanged
} from 'firebase/auth';
import { doc, getDoc, setDoc } from 'firebase/firestore';
import { auth, db } from '../config/firebase';

export const useFirebaseAuth = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // √âcouter les changements d'auth
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (firebaseUser) => {
      if (firebaseUser) {
        // R√©cup√©rer les donn√©es utilisateur depuis Firestore
        const userDoc = await getDoc(doc(db, 'users', firebaseUser.uid));
        if (userDoc.exists()) {
          const userData = userDoc.data();
          setUser({
            uid: firebaseUser.uid,
            ...userData,
            id: String(userData.id) // Convertir en string pour matcher notes/stats
          });
        }
      } else {
        setUser(null);
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  const handleLogin = async (username, password) => {
    try {
      console.log('üîê Tentative login pour:', username);

      // Cr√©er email Firebase √† partir du username
      const email = `${username.toLowerCase()}@esccappelle.com`;

      try {
        // Essayer de se connecter √† Firebase Auth
        console.log('üî• Connexion Firebase Auth...');
        const userCredential = await signInWithEmailAndPassword(auth, email, password);
        console.log('‚úÖ Connect√© √† Firebase:', userCredential.user.uid);

        // V√©rifier que le document Firestore existe
        const userDoc = await getDoc(doc(db, 'users', userCredential.user.uid));
        if (userDoc.exists()) {
          console.log('‚úÖ Utilisateur Firestore trouv√©:', userDoc.data().nom);
          return { success: true };
        } else {
          console.error('‚ùå Document Firestore manquant pour cet utilisateur');
          return { success: false, error: 'Compte incomplet, contactez l\'administrateur' };
        }

      } catch (loginError) {
        console.error('‚ùå Erreur connexion Firebase:', loginError.code);

        // Si les identifiants sont invalides, afficher un message clair
        if (loginError.code === 'auth/user-not-found' ||
            loginError.code === 'auth/invalid-credential' ||
            loginError.code === 'auth/wrong-password') {
          return {
            success: false,
            error: 'Identifiants incorrects. Si vous n\'avez pas encore de compte, contactez le coach.'
          };
        }

        return { success: false, error: 'Erreur de connexion: ' + loginError.message };
      }
    } catch (error) {
      console.error('‚ùå Erreur login:', error);
      return { success: false, error: error.message };
    }
  };

  // Fonction pour cr√©er un nouvel utilisateur (r√©serv√©e aux coachs via interface d'admin)
  const handleRegister = async (userData) => {
    try {
      const { username, password, nom, role, id } = userData;
      const email = `${username.toLowerCase()}@esccappelle.com`;

      console.log('üÜï Cr√©ation compte Firebase pour:', username);

      // Cr√©er le compte Firebase Auth
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      console.log('‚úÖ Compte Firebase cr√©√©:', userCredential.user.uid);

      // Cr√©er le document Firestore
      await setDoc(doc(db, 'users', userCredential.user.uid), {
        id: id,
        nom: nom,
        username: username,
        role: role,
        email: email,
        createdAt: new Date().toISOString()
      });
      console.log('‚úÖ Document Firestore cr√©√©');

      return { success: true };
    } catch (error) {
      console.error('‚ùå Erreur cr√©ation compte:', error);
      return { success: false, error: error.message };
    }
  };

  const handleLogout = async () => {
    try {
      await firebaseSignOut(auth);
      setUser(null);
    } catch (error) {
      console.error('‚ùå Erreur logout:', error);
    }
  };

  return {
    user,
    loading,
    handleLogin,
    handleRegister,
    handleLogout
  };
};
