import { doc, setDoc, deleteDoc, updateDoc } from 'firebase/firestore';
import { db } from '../config/firebase';

const useConvocations = () => {
  const convoquerJoueur = async (matchId, joueurId, convocations, setConvocations) => {
    const convocId = `${String(matchId)}_${String(joueurId)}`;
    await setDoc(doc(db, 'convocations', convocId), {
      id: convocId,
      matchId: String(matchId),
      userId: String(joueurId),
      convoque: true,
      statut: 'en_attente',
      createdAt: new Date()
    });
  };

  const deconvoquerJoueur = async (matchId, joueurId, convocations, setConvocations) => {
    const convocId = `${String(matchId)}_${String(joueurId)}`;
    await deleteDoc(doc(db, 'convocations', convocId));
  };

  const accepterConvocation = async (matchId, joueurId, user, convocations, setConvocations, setActionJoueurEnCours) => {
    const convocId = `${String(matchId)}_${String(joueurId)}`;
    await updateDoc(doc(db, 'convocations', convocId), {
      statut: 'accepte'
    });
  };

  const refuserConvocation = async (matchId, joueurId, user, convocations, setConvocations, setActionJoueurEnCours) => {
    const convocId = `${String(matchId)}_${String(joueurId)}`;
    await updateDoc(doc(db, 'convocations', convocId), {
      statut: 'refuse'
    });
  };

  return {
    convoquerJoueur,
    deconvoquerJoueur,
    accepterConvocation,
    refuserConvocation
  };
};

export default useConvocations;
