import React from 'react';
import { calculerMoyenne } from '../../utils/noteCalculator';
import { getCouleurEvaluation } from '../../utils/colors';

const EvaluationCoach = ({ 
  coachNom, 
  coachId, 
  evaluation, 
  setEvaluation, 
  onSave, 
  bgColor = '#ff8800',
  borderColor = '#ff8800'
}) => {
  const moyenne = calculerMoyenne(evaluation);

  return (
    <div style={{background: 'white', padding: '1.5rem', borderRadius: '0.75rem', border: '2px solid #e5e7eb'}}>
      <div style={{display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '1.5rem', paddingBottom: '1rem', borderBottom: '1px solid #e5e7eb'}}>
        <div>
          <h3 style={{fontWeight: 'bold', color: '#1f2937', margin: 0, fontSize: '1.25rem'}}>{coachNom}</h3>
          <p style={{color: '#6b7280', margin: 0, fontSize: '0.875rem'}}>
            {coachId === '1' ? 'Coach principal' : 'Coach adjoint'}
          </p>
        </div>
      </div>

      <div style={{display: 'grid', gap: '1rem'}}>
        <div style={{background: '#f9fafb', padding: '1rem', borderRadius: '0.5rem', border: '1px solid #e5e7eb'}}>
          <div style={{marginBottom: '0.75rem'}}>
            <div style={{color: '#1f2937', fontWeight: '600', marginBottom: '0.25rem', fontSize: '0.875rem'}}>Qualité des entraînements</div>
            <div style={{color: '#6b7280', fontSize: '0.75rem'}}>Organisation, pertinence, intérêt des séances</div>
          </div>
          <input
            type="number"
            min="1"
            max="10"
            step="0.5"
            placeholder="Note /10"
            value={evaluation.entrainements}
            onChange={(e) => setEvaluation({...evaluation, entrainements: e.target.value})}
            style={{width: '100%', padding: '0.75rem', border: '2px solid #e5e7eb', borderRadius: '0.5rem', fontSize: '1.25rem', textAlign: 'center', fontWeight: 'bold', background: 'white', color: '#1f2937'}}
          />
        </div>

        <div style={{background: '#f9fafb', padding: '1rem', borderRadius: '0.5rem', border: '1px solid #e5e7eb'}}>
          <div style={{marginBottom: '0.75rem'}}>
            <div style={{color: '#1f2937', fontWeight: '600', marginBottom: '0.25rem', fontSize: '0.875rem'}}>Coaching en match</div>
            <div style={{color: '#6b7280', fontSize: '0.75rem'}}>Tactique, changements, lecture du jeu</div>
          </div>
          <input
            type="number"
            min="1"
            max="10"
            step="0.5"
            placeholder="Note /10"
            value={evaluation.coaching}
            onChange={(e) => setEvaluation({...evaluation, coaching: e.target.value})}
            style={{width: '100%', padding: '0.75rem', border: '2px solid #e5e7eb', borderRadius: '0.5rem', fontSize: '1.25rem', textAlign: 'center', fontWeight: 'bold', background: 'white', color: '#1f2937'}}
          />
        </div>

        <div style={{background: '#f9fafb', padding: '1rem', borderRadius: '0.5rem', border: '1px solid #e5e7eb'}}>
          <div style={{marginBottom: '0.75rem'}}>
            <div style={{color: '#1f2937', fontWeight: '600', marginBottom: '0.25rem', fontSize: '0.875rem'}}>Relation avec les joueurs</div>
            <div style={{color: '#6b7280', fontSize: '0.75rem'}}>Communication, écoute, motivation</div>
          </div>
          <input
            type="number"
            min="1"
            max="10"
            step="0.5"
            placeholder="Note /10"
            value={evaluation.relation}
            onChange={(e) => setEvaluation({...evaluation, relation: e.target.value})}
            style={{width: '100%', padding: '0.75rem', border: '2px solid #e5e7eb', borderRadius: '0.5rem', fontSize: '1.25rem', textAlign: 'center', fontWeight: 'bold', background: 'white', color: '#1f2937'}}
          />
        </div>

        {moyenne && (
          <div style={{background: '#f9fafb', padding: '1.5rem', borderRadius: '0.5rem', textAlign: 'center', border: '2px solid #e5e7eb'}}>
            <div style={{fontSize: '0.75rem', color: '#6b7280', marginBottom: '0.5rem', fontWeight: '600', textTransform: 'uppercase', letterSpacing: '0.05em'}}>Moyenne</div>
            <div style={{fontSize: '3rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '0rem'}}>{moyenne}<span style={{fontSize: '1.5rem', color: '#6b7280'}}>/10</span></div>
          </div>
        )}

        <button
          onClick={onSave}
          style={{background: '#1f2937', color: 'white', padding: '1rem', borderRadius: '0.5rem', border: 'none', cursor: 'pointer', fontWeight: '600', fontSize: '1rem'}}
        >
          Sauvegarder
        </button>
      </div>
    </div>
  );
};

export default EvaluationCoach;
